Ext.define("Ext.rtl.view.Table",{override:"Ext.view.Table",rtlCellTpl:['<td class="'+Ext.baseCSSPrefix+'rtl {tdCls}" {tdAttr}>','<div {unselectableAttr} class="'+Ext.baseCSSPrefix+"rtl "+Ext.baseCSSPrefix+'grid-cell-inner {innerCls}"',' style="text-align:{[this.getAlign(values.align)]};<tpl if="style">{style}</tpl>">{value}</div>',"</td>",{priority:0,rtlAlign:{right:"left",left:"right",center:"center"},getAlign:function(a){return this.rtlAlign[a]}}],beforeRender:function(){var a=this;a.callParent();if(a.getHierarchyState().rtl){a.addCellTpl(a.getTpl("rtlCellTpl"))}},getCellPaddingAfter:function(a){return Ext.fly(a).getPadding(this.getHierarchyState().rtl?"l":"r")}});